// Fonction pour traduire le contenu
export function translateContent(text: string, targetLang: string): string {
  if (targetLang === 'fr') {
    return text; // Pas de traduction nécessaire pour le français
  }

  const translations = contentTranslations[targetLang as keyof typeof contentTranslations];
  if (!translations) {
    return text; // Retourne le texte original si la langue n'est pas supportée
  }

  // Chercher une traduction exacte
  if (translations[text as keyof typeof translations]) {
    return translations[text as keyof typeof translations];
  }

  // Si pas de traduction trouvée, retourner le texte original
  return text;
} 